(1, 'Tiếng Trung cho người mới bắt đầu (N5)', 'Khóa học dành cho người mới bắt đầu học tiếng Trung, giúp bạn nắm vững ngữ pháp cơ bản và các kỹ năng giao tiếp đơn giản.', 'hoat_dong', 3000000.00, 25, '3 tháng', '2025-04-03 18:08:17', '2025-04-03 18:08:17', ''),
(2, 'Tiếng Trung trung cấp (N4)', 'Khóa học tiếp nối N5, giúp học viên nâng cao kỹ năng nghe, nói, đọc, viết tiếng Trung ở trình độ trung cấp.', 'hoat_dong', 3500000.00, 30, '4 tháng', '2025-04-03 18:08:17', '2025-04-03 18:08:17', ''),
(3, 'Tiếng Trung trung cấp nâng cao (N3)', 'Khóa học giúp học viên có thể hiểu được tiếng Trung được sử dụng trong các tình huống đời thường.', 'hoat_dong', 4000000.00, 30, '4 tháng', '2025-04-03 18:08:17', '2025-04-03 18:08:17', ''),
(4, 'Tiếng Trung nâng cao (N2)', 'Khóa học nâng cao giúp học viên có khả năng đọc hiểu và nghe hiểu tiếng Trung ở mức độ gần tương đương với người bản xứ.', 'hoat_dong', 4500000.00, 35, '5 tháng', '2025-04-03 18:08:17', '2025-04-03 18:08:17', ''),
(5, 'Tiếng Trung thành thạo (N1)', 'Khóa học dành cho những học viên muốn đạt trình độ tiếng Trung cao nhất, tương đương với người bản xứ.', 'hoat_dong', 5000000.00, 40, '6 tháng', '2025-04-03 18:08:17', '2025-04-03 18:08:17', ''),
